import React, { Component } from 'react';
import Table from './components/Table.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import base64 from 'base-64'; // Import base64 correctly

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            vehicle: [],
            error: null // Added error state
        };
    }

    componentDidMount() {
        const uname = "admin";
        const pword = "admin";

        fetch('http://54.237.115.48:80/vehicle', {
            method: 'GET',
            headers: {
                'Authorization': 'Basic ' + base64.encode(uname + ":" + pword),
                'Content-Type': 'application/json',
                // Add any other necessary headers here
            }
        })
            .then(res => {
                if (!res.ok) {
                    throw new Error('Network response was not ok');
                }
                return res.json();
            })
            .then(vehicle => this.setState({ vehicle }))
            .catch(error => this.setState({ error: "Error fetching vehicles: " + error.message }));
    }

    render() {
        return (
            <div className="App">
                <nav className="navbar navbar-light bg-light">
                    <a className="navbar-brand" href="./">
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 841.9 595.3" width="40">
                            <g fill="#61DAFB">
                                {/* SVG Path */}
                            </g>
                        </svg>
                        List of all the Vehicles
                    </a>
                </nav>
                {this.state.error ? (
                    <div className="alert alert-danger" role="alert">
                        {this.state.error}
                    </div>
                ) : (
                    <Table vehicle={this.state.vehicle} />
                )}
            </div>
        );
    }
}

export default App;
